import{_ as e,c as r,o as a,d as t}from"./app.ac976fe5.js";const u=JSON.parse('{"title":"Что такое KIT, RTF, PNP, BNF..","description":"","frontmatter":{},"headers":[{"level":2,"title":"Что лучше взять новичку?","slug":"что-лучше-взять-новичку","link":"#что-лучше-взять-новичку","children":[]},{"level":2,"title":"Какой коптер выбрать?","slug":"какои-коптер-выбрать","link":"#какои-коптер-выбрать","children":[]}],"relativePath":"shop/KIT.md"}'),p={name:"shop/KIT.md"},o=t('<h1 id="что-такое-kit-rtf-pnp-bnf" tabindex="-1">Что такое KIT, RTF, PNP, BNF.. <a class="header-anchor" href="#что-такое-kit-rtf-pnp-bnf" aria-hidden="true">#</a></h1><p>Достаточно подробно описано здесь: <a href="https://profpv.ru/kvadrokopter-rtf-bnf-pnp-i-arf/" target="_blank" rel="noreferrer">https://profpv.ru/kvadrokopter-rtf-bnf-pnp-i-arf/</a> !!! В статье указаны неактуальные варианты дронов и наборов, актуально лишь описание каждого вида!!!</p><h2 id="что-лучше-взять-новичку" tabindex="-1">Что лучше взять новичку? <a class="header-anchor" href="#что-лучше-взять-новичку" aria-hidden="true">#</a></h2><p>Зависит от новичка, бюджета и хотелок. С 2020 года на рынке стали появляться неплохие бюджетные аппараты в уже собранном виде. После их появления стало не всегда выгодно собирать самому. Определенные запросы по типу &quot;лучший гоночный квад&quot; или &quot;самый долго летающий дрон&quot; требуют рассчетов, тестов, и на текущий момент нет готовых решений, нужно самостоятельно собирать. Но под основные хотелки уже собраны аппараты, и ничего плохого в покупке готового нет. Стоит лишь отметить, что важно при выборе такого проконтролировать, что дрон рассчитан на желаемое количество ячеек аккумулятора, имеет приемник нужного протокола либо без него для самостоятельного припаивания, и на борту установлена необходимая видеосистема (цифровая или аналоговая).</p><h2 id="какои-коптер-выбрать" tabindex="-1">Какой коптер выбрать? <a class="header-anchor" href="#какои-коптер-выбрать" aria-hidden="true">#</a></h2><p>Из класса тинивупов: happymodel mobula6/moblite6.</p><p>Из класса синевупов: flywoo cinerace20, geprc cinelog 25/30, betafpv pavo30 для naked gopro. geprc cinelog 30/35 для полной гопро.</p><p>Из класса зубочисток: hglrc pertel 3&quot;, betafpv hx115, geprc smart 2.5/3.5&quot;, happymodel crux 3/3.5&quot;.</p><p>Из класса гоночных: foxeer caesar, iflight mach r5, hglrc wind5.</p><p>Из класса фристайловых: iflight nazgul/evoque/chimera5/titan, geprc mark 5.</p><p>Из класса долголетов: geprc crocodile baby 4/5/7 pro, hglrc rekon 4/5/7, flywoo explorer, iflight chimera7,</p><p>Из класса синелифтеров: Quadlabs Siccario FPV Cinelifter, iFlight Taurus X8, Nurk FPV Special Edition Merica 9.</p>',12),i=[o];function n(c,l,h,d,s,f){return a(),r("div",null,i)}const g=e(p,[["render",n]]);export{u as __pageData,g as default};
